INSERT INTO `lol_noticoo` (`oid`, `oname`, `ostatus`, `oimg`, `ocont`, `ocomen`, `noticid`) VALUES (1, '紫雨林之拳', '直接生命值提高。生命值层数减少', '3052.png', '<blockquote>\r\n							<p>&nbsp; &nbsp;【紫雨林之拳】需要花太长的时间来叠加收益了。其余无话可说。</p>\r\n						</blockquote>\r\n						<p>\r\n							&nbsp; &nbsp; &nbsp; 生命值：150→200<br>&nbsp; &nbsp; &nbsp;\r\n							最大层数：30→20 (每层仍然是5点生命值)\r\n</p>', NULL, 1);
INSERT INTO `lol_noticoo` (`oid`, `oname`, `ostatus`, `oimg`, `ocont`, `ocomen`, `noticid`) VALUES (2, '上古钱币路线', '钱币回复的法力值减少', '3301.png', '【上古钱币】的法力值回复原本是为了让辅助能更频繁地使用技能，\r\n                提高他们冒着风险进入兵线拾取硬币的意愿。但奇怪的是，钱币所提供的法力值回复太过慷慨，\r\n                它并没有激起冒着预期风险的行为。当蓝色钱币太过有效时，辅助在无视部分掉落的情况下仍然可以在线上保持充足的法力值。\r\n                如果你并不会出现法力值不足的情况，为什么还要冒着受伤的风险呢(更不用说死亡)?', '回蓝能力降低，线上续航能力减弱', 1);
