INSERT INTO `shop_game` (`id`, `creater`, `name`, `comm`, `systemcfg`, `price`, `discount`, `stat`, `img`) VALUES (1, 'l', '绝地求生', '绝地求生(Playerunknown\'s Battlegrounds)，是Bluehole与《H1Z1》、《武装突袭3》“大逃杀”模式制作人Playerunknown合作的一款开放世界策略射击游戏，采用虚幻4引擎制作。这款游戏是一款大逃杀类型的游戏，每一局游戏将有100名玩家参与，他们将被投放在绝地岛(battlegrounds)的上空，在游戏的开始时所有人都一无所有。玩家需要在岛上收集各种资源，对抗其他玩家，让自己生存到最后。', '操作系统:64位Windows 7, Windows 8.1, Windows 10\r\n处理器:Intel Core i3-4340 / AMD FX-6300\r\n内存:6 GB RAM（推荐8GB起）\r\n图形:nVidia GeForce GTX 660 2GB / AMD Radeon HD 7850 2GB\r\nDirectX 版本:11\r\n网络:宽带互联网连接\r\n存储空间:需要 30 GB 可用空间', 98, 98, 1, '../image/15/header.jpg');
INSERT INTO `shop_game` (`id`, `creater`, `name`, `comm`, `systemcfg`, `price`, `discount`, `stat`, `img`) VALUES (2, 'bl', '冰冷海域', '《冰冷海域（Cold Waters）》是Killerfis工口GAMEs制作发行的一款关于现代海战的策略游戏。游戏背景是1988年经典军事小说，汤姆克兰西的《红色风暴》，玩家在冷战中指挥一艘核潜艇在第三次世界大战开始之时，绝望地阻止东西方的MAD(确保相互毁灭)。', '操作系统: XP 处理器: Intel Atom\r\n内存: 2 GB RAM\r\nDirectX 版本: 9.0c\r\n\r\n  存储空间: 需要 2 GB 可用空间\r\n\r\n  推荐配置：\r\n\r\n  操作系统: 7\r\n\r\n  处理器: Core i5\r\n内存: 4 GB RAM\r\nDirectX 版本: 9.0c\r\n存储空间: 需要 4 GB 可用空间', 120, 100, 1, '../image/1/2.jpg');
INSERT INTO `shop_game` (`id`, `creater`, `name`, `comm`, `systemcfg`, `price`, `discount`, `stat`, `img`) VALUES (3, 'igam', '反恐精英', '《反恐精英》将玩家分为“反恐精英”（Counter Terrorists）阵营与“恐怖份子”（Terrorists）阵营两队，每个队伍必须在一个地图上进行多回合的战斗。赢得回合的方法是达到该地图要求的目标，或者是完全消灭敌方玩家。', '操作系统: Windows 7处理器: 1.8GHz内存: 4 GB RAMIntel HD 4000 系列\r\nDirectX: Version 9.0\r\n硬盘空间: 4 GB可用空间\r\n推荐配置：\r\n操作系统: Windows 7\r\n处理器: 2.4GHz\r\n内存: 8 GB RAM显卡: GeForce 780\r\nDirectX: Version 10', 288, 256, 1, '../image/12/header.jpg');
