package com.Stream.entity;


public class GameCarshop extends BaseEntity implements java.io 

.Serializable  {

	// Fields

	private Long Cid;
	private Game game;
	private User user;
	private Long total;
	public GameCarshop() {
		super();
		// TODO Auto-generated constructor stub
	}
	public GameCarshop(Long cid, Game game, User user, Long total) {
		super();
		Cid = cid;
		this.game = game;
		this.user = user;
		this.total = total;
	}
	public Long getCid() {
		return Cid;
	}
	public void setCid(Long cid) {
		Cid = cid;
	}
	public Game getGame() {
		return game;
	}
	public void setGame(Game game) {
		this.game = game;
	}
	public User getUser() {
		return user;
	}
	public void setUser(User user) {
		this.user = user;
	}
	public Long getTotal() {
		return total;
	}
	public void setTotal(Long total) {
		this.total = total;
	}
}
